var section=document.querySelector(".numbers-section"),hasEntered=!1;window.addEventListener("scroll",e=>{window.scrollY+window.innerHeight-200>=section.offsetTop&&!hasEntered&&(hasEntered=!0,$(".value").each(function(){$(this).prop("Counter",0).animate({Counter:$(this).text().replace(/,/g,"")},{duration:5e3,easing:"swing",step:function(e){$(this).text(Math.ceil(e).toLocaleString())}})}))});